mapper.select:
select 

from HDMS.SYS_TABLES
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}
  and SCHEMA_NAME = #{schemaName,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=CHAR}

mapper.delete:
delete from HDMS.SYS_TABLES
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}
  and SCHEMA_NAME = #{schemaName,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=CHAR}
